def function(x, y):
    return coherence_values[int(x/20)-1][int(y/20)-1]

fig = plt.figure()
ax = fig.gca(projection='3d')
X = np.arange(20, 1020, 20)
Y = np.arange(20, 1020, 20)
X, Y = np.meshgrid(X, Y)
zs = np.array([function(x,y) for x,y in zip(np.ravel(X), np.ravel(Y))])
Z = zs.reshape(X.shape)
surf = ax.plot_surface(X, Y, Z, rstride=1, cstride=1, cmap=cm.coolwarm,
                       linewidth=0, antialiased=False)
ax.set_zlim(0, 1)

ax.set_xlabel(r'$k$ - Dimension of $\mathbb{R}^k$')
ax.set_ylabel(r'$d$ - Number of Vectors in $\mathbb{R}^k$')
ax.set_zlabel('Coherence Value')
ax.set_title(r"Coherence Values as a Function of $k$ and $d$")

ax.zaxis.set_major_locator(LinearLocator(10))
ax.zaxis.set_major_formatter(FormatStrFormatter('%.02f'))

fig.colorbar(surf, shrink=0.5, aspect=5)

plt.savefig("problem_3.png", dpi=300)

